const App = () => {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged(async (user) => {
      if (user) {
        const userDoc = await db.collection('users').doc(user.uid).get();
        setCurrentUser({ ...user, role: userDoc.data().role });
      } else {
        setCurrentUser(null);
      }
    });
    return () => unsubscribe();
  }, []);

  const handleLogout = () => {
    firebase.auth().signOut();
  };

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={currentUser ? <PrivateRoutes /> : <PublicRoutes />} />
        </Routes>
      </div>
    </Router>
  );
};

const PrivateRoutes = () => (
  <div>
    <h1>Welcome, {currentUser.email}</h1>
    <button onClick={handleLogout}>Logout</button>
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/admin" element={<AdminPanel />} />
      <Route path="/banker" element={<BankerPage />} />
      <Route path="/general_manager" element={<GeneralManagerPage />} />
      <Route path="/it_manager" element={<ITManagerPage />} />
      <Route path="/it_team" element={<ITTeamPage />} />
    </Routes>
  </div>
);

const PublicRoutes = () => (
  <div>
    <h2>Login</h2>
    <LoginForm />
  </div>
);

// Diğer bileşenler ve kodlar buraya eklenir...

export default App;
