// App.js

import React, { useState, useEffect } from 'react';
import firebase from 'firebase/app';
import 'firebase/auth';
import 'firebase/firestore';


// Firebase projenize ait yapılandırma bilgileri
const firebaseConfig = {
  // Firebase config bilgileriniz buraya gelecek
};

// Firebase projenize bağlanma
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

const db = firebase.firestore();

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);

  // Kullanıcı durumu değiştiğinde çağrılan fonksiyon
  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged(async (user) => {
      if (user) {
        const userDoc = await db.collection('users').doc(user.uid).get();
        setCurrentUser({ ...user, role: userDoc.data().role });
      } else {
        setCurrentUser(null);
      }
    });
    return () => unsubscribe();
  }, []);

  // Kullanıcı çıkışı fonksiyonu
  const handleLogout = () => {
    firebase.auth().signOut();
  };

  return (
    <div>
      {currentUser ? (
        <div>
          <h1>Welcome, {currentUser.email}</h1>
          <button onClick={handleLogout}>Logout</button>
          {currentUser.role === 'banker' && <BankerPage />}
          {currentUser.role === 'general_manager' && <GeneralManagerPage />}
          {currentUser.role === 'it_manager' && <ITManagerPage />}
          {currentUser.role === 'it_team' && <ITTeamPage />}
          {currentUser.role === 'admin' && <AdminPanel />}
        </div>
      ) : (
        <div>
          <h2>Login</h2>
          <LoginForm />
        </div>
      )}
    </div>
  );
};

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await firebase.auth().signInWithEmailAndPassword(email, password);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button type="submit">Login</button>
      {error && <p>{error}</p>}
    </form>
  );
};

const AdminPanel = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const usersSnapshot = await db.collection('users').get();
      const fetchedUsers = usersSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setUsers(fetchedUsers);
    };

    fetchUsers();
  }, []);

  const handleRoleChange = async (userId, newRole) => {
    await db.collection('users').doc(userId).update({ role: newRole });
    // Role güncellendikten sonra yeniden kullanıcıları getir
    fetchUsers();
  };

  return (
    <div>
      <h2>Admin Panel</h2>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.email} - {user.role}
            <select onChange={(e) => handleRoleChange(user.id, e.target.value)}>
              <option value="banker">Banker</option>
              <option value="general_manager">General Manager</option>
              <option value="it_manager">IT Manager</option>
              <option value="it_team">IT Team</option>
            </select>
          </li>
        ))}
      </ul>
    </div>
  );
};

const BankerPage = () => (
  <div>
    <h2>Banker Page</h2>
    {/* Banker sayfası içeriği buraya gelecek */}
  </div>
);

const GeneralManagerPage = () => (
  <div>
    <h2>General Manager Page</h2>
    {/* Genel müdür sayfası içeriği buraya gelecek */}
  </div>
);

const ITManagerPage = () => (
  <div>
    <h2>IT Manager Page</h2>
    {/* IT yöneticisi sayfası içeriği buraya gelecek */}
  </div>
);

const ITTeamPage = () => (
  <div>
    <h2>IT Team Page</h2>
    {/* IT ekibi sayfası içeriği buraya gelecek */}
  </div>
);

export default App;
