import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";

// Firebase projenize ait yapılandırma bilgileri
const firebaseConfig = {
  apiKey: "AIzaSyDGxBN6F0v7o68gcOXCyCmgOkQP293yZj0",
  authDomain: "tryapp-6176e.firebaseapp.com",
  projectId: "tryapp-6176e",
  storageBucket: "tryapp-6176e.appspot.com",
  messagingSenderId: "8839635440",
  appId: "1:8839635440:web:3fbf045edf66aa6b607d35",
  measurementId: "G-MRNY3XCE1Z"
};
// Initialize Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

// Firebase projenize bağlanma
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

const db = firebase.firestore();

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);

  // Kullanıcı durumu değiştiğinde çağrılan fonksiyon
  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged(async (user) => {
      if (user) {
        const userDoc = await db.collection('users').doc(user.uid).get();
        setCurrentUser(userDoc.exists ? { ...user, role: userDoc.data().role } : null);
      } else {
        setCurrentUser(null);
      }
    });
    return () => unsubscribe();
  }, []);

  // Kullanıcı çıkışı fonksiyonu
  const handleLogout = () => {
    firebase.auth().signOut();
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/login">Login</Link>
            </li>
            <li>
              <Link to="/register">Register</Link>
            </li>
            {currentUser && currentUser.role === "admin" && (
              <li>
                <Link to="/admin">Admin Panel</Link>
              </li>
            )}
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<LoginForm />} />
          <Route path="/register" element={<RegisterForm />} />
          {currentUser && currentUser.role === "admin" ? (
            <Route path="/admin" element={<AdminPanel currentUser={currentUser} />} />
          ) : (
            <Route path="*" element={<UserPage />} />
          )}
        </Routes>
      </div>
    </Router>
  );
};

const Home = () => {
  return (
    <div>
      <h1>Welcome to the Home Page</h1>
    </div>
  );
};

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await firebase.auth().signInWithEmailAndPassword(email, password);
      // Kullanıcı başarıyla giriş yaptığında, belirli bir sayfaya yönlendir
      window.location.href = '/'; // Örnek olarak ana sayfaya yönlendirme yapıldı
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button type="submit">Login</button>
      {error && <p>{error}</p>}
    </form>
  );
};

const RegisterForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      // Yeni kullanıcı kaydı
      const userCredential = await firebase.auth().createUserWithEmailAndPassword(email, password);

      // Kullanıcının UID'sini al
      const userId = userCredential.user.uid;

      // Firestore'a yeni belge ekle
      await db.collection('users').doc(userId).set({
        email: email,
        role: 'user' // Varsayılan rolü belirleyin
      });

    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button type="submit">Register</button>
      {error && <p>{error}</p>}
    </form>
  );
};


const AdminPanel = ({ currentUser }) => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const usersSnapshot = await db.collection('users').get();
      const fetchedUsers = usersSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setUsers(fetchedUsers);
    };

    fetchUsers();
  }, []);

  // Admin panelinde rol atama
const handleRoleChange = async (userId, newRole) => {
  await db.collection('users').doc(userId).update({
    role: newRole
  });

  // Sadece admin rolüne sahip kullanıcılar isAdmin alanını güncelleyebilir
  if (currentUser.role === 'admin') {
    await db.collection('users').doc(userId).update({
      isAdmin: newRole === 'admin'
    });
  }
};

// Kullanıcı giriş yaptığında rol bazlı yönlendirme
const redirectToDashboard = (role) => {
  if (role === 'admin') {
    history.push('/admin');
  } else {
    history.push('/home');
  }
};


  return (
    <div>
      <h2>Admin Panel</h2>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.email} - {user.role}
            <select value={user.role} onChange={(e) => handleRoleChange(user.id, e.target.value)}>
              <option value="banker">Banker</option>
              <option value="general_manager">General Manager</option>
              <option value="it_manager">IT Manager</option>
              <option value="it_team">IT Team</option>
            </select>
          </li>
        ))}
      </ul>
    </div>
  );
};

const UserPage = () => {
  return (
    <div>
      <h2>User Page</h2>
      <p>You are not authorized to view this page.</p>
    </div>
  );
};

export default App;
