import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import './register.css'; // CSS dosyasını bileşene ekleyin

const RegisterForm = ({ db }) => {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const userCredential = await firebase.auth().createUserWithEmailAndPassword(email, password);

      const userId = userCredential.user.uid;

      await db.collection('users').doc(userId).set({
        name: name,
        surname: surname,
        email: email,
        role: 'user'
      });

    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="all-register"> {/* Dış div'i ekleyin ve CSS sınıfını uygulayın */}
      <div className="container-register"> {/* İç div'i ekleyin ve CSS sınıfını uygulayın */}
        <h2>Register</h2>
        <form className="register-form" onSubmit={handleRegister}> {/* Formu sarmalayın ve CSS sınıfını uygulayın */}
          <label className="label-register" htmlFor="name">Name</label>
          <input className="input-register" type="text" id="name" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
          <label className="label-register" htmlFor="surname">Surname</label>
          <input className="input-register" type="text" id="surname" placeholder="Surname" value={surname} onChange={(e) => setSurname(e.target.value)} />
          <label className="label-register" htmlFor="email">Email</label>
          <input className="input-register" type="email" id="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <label className="label-register" htmlFor="password">Password</label>
          <input className="input-register" type="password" id="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button className="button-register" type="submit">Register</button>
          {error && <p>{error}</p>}
        </form>
        <div className="login-link"> {/* Login linkini sarmalayın ve CSS sınıfını uygulayın */}
          <p>Already have an account? <a href="/login">Login</a></p>
        </div>
      </div>
    </div>
  );
};

export default RegisterForm;
