import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';

const ProfileChange = ({ currentUser }) => {
  const [newFirstName, setNewFirstName] = useState('');
  const [newLastName, setNewLastName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleUpdateProfile = async () => {
    try {
      if (newFirstName && newLastName) {
        await firebase.firestore().collection('users').doc(currentUser.uid).update({
          name: newFirstName,
          surname: newLastName
        });
        alert('Profile updated successfully!');
      } else {
        setErrorMessage('Please enter both first name and last name.');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      setErrorMessage('Failed to update profile. Please try again later.');
    }
  };

  return (
    <div>
      <h2>Edit Profile</h2>
      <div>
        <label htmlFor="newFirstName">New First Name:</label>
        <input
          type="text"
          id="newFirstName"
          value={newFirstName}
          onChange={(e) => setNewFirstName(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="newLastName">New Last Name:</label>
        <input
          type="text"
          id="newLastName"
          value={newLastName}
          onChange={(e) => setNewLastName(e.target.value)}
        />
      </div>
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      <button onClick={handleUpdateProfile}>Update Profile</button>
    </div>
  );
};

export default ProfileChange;
