import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './forgotpassword.css';

const ForgotPasswordForm = () => {
  const [email, setEmail] = useState('');
  const [code, setCode] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [showResetPasswordForm, setShowResetPasswordForm] = useState(false);
  const [currentForm, setCurrentForm] = useState('sendResetCode');

  const handleSendResetCode = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');

    try {
      const response = await axios.post('http://localhost:5265/api/passwordreset/send-reset-code', {
        email: email
      });

      if (response.data.success) {
        setSuccessMessage(response.data.message);
        setShowResetPasswordForm(true);
        setCurrentForm('resetPassword');
      } else {
        setErrorMessage(response.data.message);
      }
    } catch (error) {
      if (error.response) {
        setErrorMessage(error.response.data.message);
      } else {
        setErrorMessage("Error while making the request");
      }
    }
  };

  const handleResetPassword = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');

    try {
      const response = await axios.post('http://localhost:5265/api/passwordreset/reset-password', {
        email: email,
        code: code,
        newPassword: newPassword
      });

      if (response.data.success) {
        setSuccessMessage(response.data.message);
      } else {
        setErrorMessage(response.data.message);
      }
    } catch (error) {
      if (error.response) {
        setErrorMessage(error.response.data.message);
      } else {
        setErrorMessage("Error while making the request");
      }
    }
  };

  return (
    <div className="forgot-password-container">
      <form className="forgot-password-form" onSubmit={handleSendResetCode} style={{ display: currentForm === 'sendResetCode' ? 'block' : 'none' }}>
        <div className="forgot-password-input-container">
          <input 
            type="email" 
            className="forgot-password-input" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
        </div>
        <button type="submit" className="forgot-password-button">Send Reset Code</button>
        {errorMessage && <p className="forgot-password-error">{errorMessage}</p>}
        {successMessage && <p className="forgot-password-success">{successMessage}</p>}
        <Link to="/login" className="back-to-login">Back to Login</Link>
      </form>

      <form className="forgot-password-form" onSubmit={handleResetPassword} style={{ display: currentForm === 'resetPassword' ? 'block' : 'none' }}>
        <div className="forgot-password-input-container">
          <input 
            type="text" 
            className="forgot-password-input" 
            placeholder="Verification Code" 
            value={code} 
            onChange={(e) => setCode(e.target.value)} 
            required 
          />
        </div>
        <div className="forgot-password-input-container">
          <input 
            type="password" 
            className="forgot-password-input" 
            placeholder="New Password" 
            value={newPassword} 
            onChange={(e) => setNewPassword(e.target.value)} 
            required 
          />
        </div>
        <button type="submit" className="forgot-password-button">Reset Password</button>
        {errorMessage && <p className="forgot-password-error">{errorMessage}</p>}
        {successMessage && <p className="forgot-password-success">{successMessage}</p>}
        <Link to="/login" className="back-to-login">Back to Login</Link>
      </form>
      {!showResetPasswordForm && (
        <button className="reset-password-button" onClick={() => setShowResetPasswordForm(true)}>Reset Password</button>
      )}
    </div>
  );
};

export default ForgotPasswordForm;
