import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics"; 
import RegisterForm from './RegisterForm';
import LoginForm from './LoginForm';
import AdminPanel from './AdminPanel';
import Profile from './Profile'; // Yeni bileşeni ekleyin

const firebaseConfig = {
  // Firebase konfigürasyon bilgileri
};

const app = initializeApp(firebaseConfig);

if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

const db = firebase.firestore();

const Root = () => {
  return (
    <Router>
      <App />
    </Router>
  );
};

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged(async (user) => {
      if (user) {
        const userDoc = await db.collection('users').doc(user.uid).get();
        setCurrentUser(userDoc.exists ? { ...user, email: user.email, role: userDoc.data().role } : null);

        if (userDoc.exists) {
          switch (userDoc.data().role) {
            // Kullanıcı rolüne göre yönlendirme yapılabilir
          }
        }
      } else {
        setCurrentUser(null);
      }
    });
    return () => unsubscribe();
  }, []);

  const handleLogout = () => {
    firebase.auth().signOut();
    navigate('/');
  };

  return (
    <div>
      <nav>
        {/* Navbar bileşeni */}
      </nav>
      <div>
        {/* Profil bilgileri */}
      </div>
      <Routes>
        {/* Rotalar */}
      </Routes>
    </div>
  );
};

export default Root;
