// Register.js

import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import './register.css';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const userCredential = await firebase.auth().createUserWithEmailAndPassword(email, password);
      const userId = userCredential.user.uid;
      await firebase.firestore().collection('users').doc(userId).set({
        email: email,
        role: 'user'
      });
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="all-register">
      <div className="container-register">
        <h2>Register</h2>
        <form onSubmit={handleRegister} className="register-form">
          <label className="label-register">Email:</label>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="input-register" />
          <label className="label-register">Password:</label>
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="input-register" />
          <button type="submit" className="button-register">Register</button>
          {error && <p>{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default Register;
