import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './forgotpassword.css'; // forgotpassword.css dosyas覺n覺 import ediyoruz

const ForgotPasswordForm = () => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    try {
      const response = await axios.post('http://localhost:5265/api/passwordreset/send-reset-code', {
        email: email
      });

      setSuccessMessage(response.data.message);
    } catch (error) {
      if (error.response) {
        setError(error.response.data.message);
      } else {
        setError("Error while making the request");
      }
    }
  };

  return (
    <div className="forgot-password-container">
      <form className="forgot-password-form" onSubmit={handleForgotPassword}>
        <div className="forgot-password-input-container">
          <input 
            type="email" 
            className="forgot-password-input" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
        </div>
        <button type="submit" className="forgot-password-button">Send Reset Code</button>
        {error && <p className="forgot-password-error">{error}</p>}
        {successMessage && <p className="forgot-password-success">{successMessage}</p>}
        <Link to="/login" className="back-to-login">Back to Login</Link>
      </form>
    </div>
  );
};

export default ForgotPasswordForm;
