import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import axios from 'axios'; 

const Root = () => {
  return (
    <Router>
      <App />
    </Router>
  );
};

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Firebase ile oturum durumunu izlemek yerine, backend API'den kullanıcı bilgilerini alıyoruz
    const fetchUserData = async () => {
      try {
        const response = await axios.get('/api/user'); // Kullanıcı bilgilerini getiren bir API endpoint'i kullanılmalı
        setCurrentUser(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
        setCurrentUser(null);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post('/api/logout'); // Oturumu kapatmak için bir API endpoint'i kullanılmalı
      setCurrentUser(null);
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/login">Login</Link>
          </li>
          <li>
            <Link to="/register">Register</Link>
          </li>
          {currentUser && (
            <>
              <li>
                <button onClick={handleLogout}>Logout</button>
              </li>
            </>
          )}
        </ul>
      </nav>
      <div>
        {currentUser !== null && (
          <div>
            <p>Email: {currentUser.email}</p>
            <p>Role: {currentUser.role}</p>
          </div>
        )}
      </div>
      <Routes>
        {/* Diğer route'ları buraya ekle */}
      </Routes>
    </div>
  );
};

const Home = () => {
  return (
    <div>
      <h1>Welcome to the Home Page</h1>
    </div>
  );
};

export default Root;
