import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics"; 

const firebaseConfig = {
  apiKey: "AIzaSyDGxBN6F0v7o68gcOXCyCmgOkQP293yZj0",
  authDomain: "tryapp-6176e.firebaseapp.com",
  databaseURL: "https://tryapp-6176e-default-rtdb.firebaseio.com",
  projectId: "tryapp-6176e",
  storageBucket: "tryapp-6176e.appspot.com",
  messagingSenderId: "8839635440",
  appId: "1:8839635440:web:3fbf045edf66aa6b607d35",
  measurementId: "G-MRNY3XCE1Z"
};

const app = initializeApp(firebaseConfig);

if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

const db = firebase.firestore();

const Root = () => {
  return (
    <Router>
      <App />
    </Router>
  );
};

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged(async (user) => {
      if (user) {
        const userDoc = await db.collection('users').doc(user.uid).get();
        setCurrentUser(userDoc.exists ? { ...user, email: user.email, role: userDoc.data().role } : null);

        if (userDoc.exists) {
          switch (userDoc.data().role) {
            case 'user':
              navigate('/user');
              break;
            case 'admin':
              navigate('/admin');
              break;
            case 'it_manager':
              navigate('/it_manager');
              break;
            case 'it_staff':
              navigate('/it_staff');
              break;
            case 'general_manager':
              navigate('/general_manager');
              break;
            case 'banker':
              navigate('/banker');
              break;
            default:
              navigate('/');
              break;
          }
        }
      } else {
        setCurrentUser(null);
      }
    });
    return () => unsubscribe();
  }, [navigate]); // Added navigate to dependency array

  const handleLogout = () => {
    firebase.auth().signOut();
    navigate('/');
  };

  return (
    <div>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/login">Login</Link>
          </li>
          <li>
            <Link to="/register">Register</Link>
          </li>
          {currentUser && (
            <>
              {currentUser.role === 'admin' && (
                <li>
                  <Link to="/admin">Admin Panel</Link>
                </li>
              )}
              {currentUser.role === 'it_manager' && (
                <li>
                  <Link to="/it_manager">IT Manager Page</Link>
                </li>
              )}
              {currentUser.role === 'it_staff' && (
                <li>
                  <Link to="/it_staff">IT Staff Page</Link>
                </li>
              )}
              {currentUser.role === 'general_manager' && (
                <li>
                  <Link to="/general_manager">General Manager Page</Link>
                </li>
              )}
              {currentUser.role === 'banker' && (
                <li>
                  <Link to="/banker">Banker Page</Link>
                </li>
              )}
              <li>
                <button onClick={handleLogout}>Logout</button>
              </li>
            </>
          )}
        </ul>
      </nav>
      <div>
        {currentUser !== null && (
          <div>
            <p>Email: {currentUser.email}</p>
            <p>Role: {currentUser.role}</p>
          </div>
        )}
      </div>
      <Routes>
        <Route path="/" element={<Home currentUser={currentUser} />} />
        <Route path="/login" element={<LoginForm />} />
        <Route path="/register" element={<RegisterForm />} />
        {currentUser && (
          <>
            {currentUser.role === 'user' && (
              <Route path="/user" element={<UserPage currentUser={currentUser} />} />
            )}
            {currentUser.role === 'admin' && (
              <Route path="/admin" element={<AdminPanel currentUser={currentUser} />} />
            )}
            {currentUser.role === 'it_manager' && (
              <Route path="/it_manager" element={<ITManagerPage />} />
            )}
            {currentUser.role === 'it_staff' && (
              <Route path="/it_staff" element={<ITStaffPage />} />
            )}
            {currentUser.role === 'general_manager' && (
              <Route path="/general_manager" element={<GeneralManagerPage />} />
            )}
            {currentUser.role === 'banker' && (
              <Route path="/banker" element={<BankerPage />} />
            )}
          </>
        )}
      </Routes>
    </div>
  );
};

const Home = () => {
  return (
    <div>
      <h1>Welcome to the Home Page</h1>
    </div>
  );
};

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await firebase.auth().signInWithEmailAndPassword(email, password);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button type="submit">Login</button>
      {error && <p>{error}</p>}
    </form>
  );
};

const RegisterForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const userCredential = await firebase.auth().createUserWithEmailAndPassword(email, password);

      const userId = userCredential.user.uid;

      await db.collection('users').doc(userId).set({
        email: email,
        role: 'user'
      });

    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button type="submit">Register</button>
      {error && <p>{error}</p>}
    </form>
  );
};

const AdminPanel = ({ currentUser }) => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const usersSnapshot = await db.collection('users').get();
      const fetchedUsers = usersSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setUsers(fetchedUsers);
    };

    fetchUsers();
  }, []);

  const handleRoleChange = async (userId, newRole) => {
    if (currentUser.role === 'admin') {
      await db.collection('users').doc(userId).update({ role: newRole });
      setUsers(prevUsers => prevUsers.map(user =>
        user.id === userId ? { ...user, role: newRole } : user
      ));
    }
  };

  const handleDeleteUser = async (userId) => {
    if (currentUser.role !== 'admin') {
      return; // Prevent non-admins from deleting users
    }

    if (userId === currentUser.id) {
      alert("You cannot delete yourself!");
      return;
    }

    if (window.confirm(`Are you sure you want to delete user ${userId}? This action cannot be undone.`)) {
      try {
        await db.collection('users').doc(userId).delete();

        setUsers(prevUsers => prevUsers.filter(user => user.id !== userId));
      } catch (error) {
        alert(`Error deleting user: ${error.message}`);
      }
    }
  };

  return (
    <div>
      <h2>Admin Panel</h2>
      <p>Current User: {currentUser.email}</p>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.email} - {user.role}
            {currentUser.role === 'admin' && user.role !== 'admin' && (
              <>
                <select value={user.role} onChange={(e) => handleRoleChange(user.id, e.target.value)}>
                  <option value="user">User</option>
                  <option value="admin">Admin</option>
                  <option value="banker">Banker</option>
                  <option value="general_manager">General Manager</option>
                  <option value="it_manager">IT Manager</option>
                  <option value="it_staff">IT Staff</option>
                </select>
                <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
              </>
            )}
            {currentUser.role === 'admin' && currentUser.id === user.id && (
              <span>You cannot change your own role</span>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

const UserPage = ({ currentUser }) => {
  return (
    <div>
      <h1>Welcome to the User Page, {currentUser.email}</h1>
      <p>Your role: {currentUser.role}</p>
    </div>
  );
};

const ITManagerPage = () => {
  return (
    <div>
      <h1>Welcome to the IT Manager Page</h1>
    </div>
  );
};

const ITStaffPage = () => {
  return (
    <div>
      <h1>Welcome to the IT Staff Page</h1>
    </div>
  );
};

const GeneralManagerPage = () => {
  return (
    <div>
      <h1>Welcome to the General Manager Page</h1>
    </div>
  );
};

const BankerPage = () => {
  return (
    <div>
      <h1>Welcome to the Banker Page</h1>
    </div>
  );
};

export default Root;
